import { useState, useContext } from "react";
import "./SingleProduct.scss";

import RelatedProducts from "./RelatedProducts/RelatedProducts"
import { FaFacebookF, FaTwitter, FaInstagram, FaTelegram, FaPinterest, FaCartPlus } from "react-icons/fa"
import prod from "../../assets/products/braclet-prod-2.jpg"

import useFetch from "../../hooks/useFetch";
import { useParams } from "react-router-dom"
import { Context } from "../../utils/context";


const SingleProduct = () => {
    const [quantity, setQuantity] = useState(1)
    const { id } = useParams()
    const { data } = useFetch(`/api/products?populate=*&[filters][id]=${id}`)
    const { handleAddToCart } = useContext(Context)

    const increment = () => {
        setQuantity((prevState) => prevState + 1)
    }

    const decrement = () => {
        setQuantity((prevState) => {
            if (prevState == 1) return 1
            return prevState - 1
        })
    }

    if (!data) return;

    const product = data.data[0].attributes
    return (
        <div className="single-product-main-content">
            <div className="layout">
                <div className="single-product-page">
                    <div className="left">
                        <img src={process.env.REACT_APP_DEV_URL +
                            product.img.data[0].attributes.url} alt="" />
                    </div>
                    <div className="right">
                        <span className="name">{product.title}</span>
                        <span className="price">{product.price}&#36;</span>
                        <span className="disc">{product.desc}</span>

                        <div className="cart-buttons">
                            <div className="quantity-buttons">
                                <span onClick={decrement}>-</span>
                                <span>{quantity}</span>
                                <span onClick={increment}>+</span>
                            </div>
                            <button className="add-to-cart-button"><FaCartPlus size={20} /> ADD TO CART</button>
                        </div>

                        <span className="divider"></span>

                        <div className="info-items">
                            <span className="text-bold">
                                Category:
                                <span> {product.categories.data[0].attributes.title}</span>
                            </span>
                            <span className="text-bold">
                                Share:
                                <span className="social-icons">
                                    <FaFacebookF />
                                    <FaTwitter />
                                    <FaInstagram />
                                    <FaTelegram />
                                    <FaPinterest />
                                </span>
                            </span>
                        </div>

                    </div>
                </div>
                <RelatedProducts productId={id} categoryId={product.categories.data[0].id} />
            </div>
        </div>
    )
};

export default SingleProduct;
